import React from 'react'
import Header from './jsx/Header'
import CreateNotes from './jsx/CreateNotes'
import Notes from './jsx/Notes'
import Footer from './jsx/Footer'
import { useState } from 'react'


const App = () => {
const [additem, setadditem] =useState([]);

  const addnote = (item) => {
    // alert("clicked")

    setadditem((prevData)=>{
      return[ ...prevData, item ]
          
    })
    console.log(item);
  }


  const deleteitems = (id) => {

    setadditem((oldData)=>{
      return oldData.filter((arr, ind)=>{
        return ind !== id
      })
    })

  }

  return (
    <>
        <Header/>
        <CreateNotes passitem={addnote}/>
        
        {additem.map((curEle,index)=>{
          return(
            <Notes
            key={index}
            id={index}
            title={curEle.title}
            content={curEle.content}
            delete={deleteitems}

            />
          )
        })}

        <Footer />
    </>
  )
}

export default App
