import ComA from './ComA'


const FirstName = createContext();
const LastName = createContext();


const App = () => {
  return (
    <>
       
      <FirstName.Provider value={"abhishek"}> 
        <LastName.Provider value={"kumar"}>
           <ComA />
        </LastName.Provider>
      </FirstName.Provider>
      

    </>

  )
}

export default App;
export { FirstName, LastName };



