import React, { createContext } from 'react'
import ComA from './ComA'


const FirstName = createContext();
const LastName = createContext();


const App = () => {
  return (
    <>
      <LastName></LastName>
      <FirstName.Provider value={"abhishek"}> 
          <ComA />
      </FirstName.Provider>

    </>

  )
}

export default App;
export { FirstName };



